import { useEffect, useState } from "react";

const EditTask = ({ task, index, taskList, setTaskList }) => {
  const [editModal, setEditModal] = useState(false);
  const [projectName, setProjectName] = useState("");
  const [taskDescription, setTaskDescription] = useState("");

  // ✅ Load existing task values into the form when modal opens
  useEffect(() => {
    if (editModal && task) {
      setProjectName(task.projectName || "");
      setTaskDescription(task.taskDescription || "");
    }
  }, [editModal, task]);

  const handleInput = (e) => {
    const { name, value } = e.target;
    if (name === "projectName") setProjectName(value);
    if (name === "taskDescription") setTaskDescription(value);
  };

  const handleUpdate = (e) => {
    e.preventDefault();

    const updatedTasks = [...taskList];
    updatedTasks[index] = { projectName, taskDescription };

    setTaskList(updatedTasks);
    setEditModal(false);
  };

  return (
    <>
      <button
        className="bg-gray-400 text-white text-sm uppercase
        font-semibold py-1.5 px-3 rounded-lg"
        onClick={() => setEditModal(true)}
      >
        Edit
      </button>

      {editModal && (
        <div className="flex items-center justify-center 
        overflow-x-hidden overflow-y-auto
        fixed inset-0 z-100">
          <div className="w-9/12 max-w-lg bg-white rounded-lg
          shadow-md relative flex flex-col">
            
            <div className="flex flex-row justify-between
            p-5 border-b border-slate-200 rounded-t">
              <h3 className="text-3xl font-semibold">Edit Task</h3>
              <button
                className="px-1 text-gray-400 float-right text-3xl
                leading-none font-semibold block"
                onClick={() => setEditModal(false)}
              >
                x
              </button>
            </div>

            <form className="px-6 pt-6 pb-4">
              <div>
                <label className="tracking-wide uppercase text-gray-700
                text-xs font-semibold mb-2 block">
                  Project Name
                </label>
                <input
                  className="w-full bg-gray-200 text-gray-700 
                  border border-gray-200 rounded py-3 px-4 mb-5"
                  name="projectName"
                  type="text"
                  placeholder="Project Name"
                  value={projectName}
                  onChange={handleInput}
                  required
                />
              </div>

              <div>
                <label className="tracking-wide uppercase text-gray-700
                text-xs font-semibold mb-2 block">
                  Task Description
                </label>
                <textarea
                  className="w-full bg-gray-200 text-gray-700 
                  border border-gray-200 rounded py-3 px-4 mb-5"
                  name="taskDescription"
                  rows="5"
                  placeholder="Task description"
                  value={taskDescription}
                  onChange={handleInput}
                />
              </div>
            </form>

            <div className="flex justify-end p-6 border-t border-slate-200 rounded-b">
              <button
                className="bg-blue-500 text-white font-semibold
                uppercase text-sm px-6 py-3 rounded hover:opacity-70"
                onClick={handleUpdate}
              >
                Update Task
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default EditTask;
